<?php#=========================================================# Исполняемый файл для авторизации#=========================================================session_start();$errors = [];#$connect_to_register = mysqli_connect(hostname:'localhost', username:'a0558948_crud', password:'2u27Ub9h', database:'a0558948_crud');$connect_to_register = mysqli_connect(hostname:'127.0.0.1:3307', username:'root', password:'root', database:'crm_materia_vremeni');$now_user = [];if(!empty($_SESSION['user'])){    session_destroy();    header('Location: regestrazi.php');}if (!empty($_POST['login']) && !empty($_POST['password'])) {    $l_login = $_POST['login'];    $p_password = $_POST['password'];    $result = $connect_to_register->query("SELECT * FROM `users` WHERE `login` = '$l_login'");    $now_user = $result->fetch_assoc();    if(empty($now_user['pass'])){        $pass = 1;    }else {        $pass = $now_user['pass'];    }    if(password_verify($p_password, $pass)){        $_SESSION['state'] = $now_user['state'];        $_SESSION['name'] = $now_user['name'];        $_SESSION['phone'] = $now_user['phone'];        $_SESSION['email'] = $now_user['email'];        $_SESSION['id_user'] = $now_user['id'];        $_SESSION['user'] = ['login' => $_POST['login'], 'password' => $_POST['password']];        header("Location: pink.php");        die;    }else{        $errors[] = 'Неверный логин или пароль';    }}?><!DOCTYPE html><html><head>    <meta name="viewport" content="width=device-width, initial-scale=1">    <meta charset="utf-8">    <link rel="stylesheet" href="assets/css/style_for_authorization/authorization.css">    <title>Pink</title>    <script src="https://kit.fontawesome.com/58ebeca16e.js" crossorigin="anonymous"></script>    <script src="assets/script/app.js" defer></script></head><body>    <form method="POST" action="regestrazi.php">		<div class="main">            <div class="title">Вход</div>            <div clas